import React, { useState, useEffect } from 'react';
import Alert from '@mui/material/Alert';
import CheckIcon from '@mui/icons-material/Check';
import Container from '@mui/material/Container';
import Button from '@mui/material/Button';
import SendIcon from '@mui/icons-material/Send';
import Box from '@mui/material/Box';
import io from 'socket.io-client';
import TextField from '@mui/material/TextField';

const socket = io('http://localhost:3000', {
    transports: ['websocket']
 });

socket.connect((res) => {
    console.log(res, "socket connected");
  });

  socket.on("connect_error", (err) => {
    console.log(`connect_error due to dededed ${err.message}`);
  });

  socket.on("connect", () => {
    console.log("socket connected");
  });

  socket.on("disconnect", () => {
    console.log("socket disconnected");
  });


const Chat = () => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  useEffect(() => {
    socket.on('message', (data) => {
    console.log('francys socket.on front: ', data);
      setMessages([...messages, data]);
    });
  }, [messages]);

  const sendMessage = () => {
    socket.emit('message', newMessage);
    console.log('francys front: ', newMessage);
    setNewMessage('');
  };

  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: '2-digit', day: '2-digit', hour: '2-digit', minute:'2-digit', second:'2-digit'};
    return new Date(dateString).toLocaleDateString(undefined, options);
  }

  const divStyle = {
    marginBottom: '2px', 
  };

  const inputDivStyle = {
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
    gap: '10px',
    marginTop: '20px',
  };

  const chatDivStyle = {
    marginTop: '20px',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'flex-end',
    alignItems: 'flex-end',
    gap: '10px',
   // minHeight: '400px',
    height: '400px',
    overflowY: 'scroll',
  };

  return (
        <Container fixed>
            <Box sx={{ height: '100vh' }}>



      <div style={chatDivStyle}>
      {messages.map((msg, index) => (
        <div style={divStyle}>
        <Alert icon={<CheckIcon fontSize="inherit" />} severity="success" key={index}>
            {formatDate(msg.date)} - {msg.message}
        </Alert>
        </div>
        ))}
      </div>
      <div style={inputDivStyle}>
        <TextField id="outlined-basic" label="Type your msg here" variant="outlined" value={newMessage} onChange={(e) => setNewMessage(e.target.value)} />
        <Button onClick={sendMessage} variant="contained" endIcon={<SendIcon />}>
        Send
        </Button>
      </div>
      </Box>
      </Container>
  );
};

export default Chat;
