import React, { useState, useEffect } from 'react';
//import Alert from '@material-ui/lab/Alert';
//import CheckIcon from '@material-ui/icons/Check';
//import { Alert, CheckIcon } from '@mui/material';
import Alert from '@mui/material/Alert';
import CheckIcon from '@mui/icons-material/Check';
import Container from '@mui/material/Container';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import StyledPaper from '@mui/material/Paper';
import Avatar from '@mui/material/Avatar';
import Box from '@mui/material/Box';
import io from 'socket.io-client';

const socket = io('http://localhost:3000', {
    transports: ['websocket']
 });

socket.connect((res) => {
    console.log(res, "socket connected");
  });

  socket.on("connect_error", (err) => {
    console.log(`connect_error due to dededed ${err.message}`);
  });

  socket.on("connect", () => {
    console.log("socket connected");
  });

  socket.on("disconnect", () => {
    console.log("socket disconnected");
  });


const Chat = () => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  useEffect(() => {
    socket.on('message', (data) => {
    console.log('francys socket.on front: ', data);
      setMessages([...messages, data]);
    });
  }, [messages]);

  const sendMessage = () => {
    socket.emit('message', newMessage);
    console.log('francys front: ', newMessage);
    setNewMessage('');
  };

  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: '2-digit', day: '2-digit', hour: '2-digit', minute:'2-digit', second:'2-digit'};
    return new Date(dateString).toLocaleDateString(undefined, options);
  }

  return (
    <div>
        <Container fixed>
            <Box sx={{ flexGrow: 1, overflow: 'hidden', px: 3 }}>
                <StyledPaper
                sx={{
                    my: 1,
                    mx: 'auto',
                    p: 2,
                }}
                >
                {messages.map((msg, index) => (
                <Grid container wrap="nowrap" spacing={2}>
                    <Grid item>
                    {formatDate(msg.date)}
                    </Grid>
                    <Grid item xs zeroMinWidth>
                    <Typography noWrap>{msg.message}</Typography>
                    </Grid>
                </Grid>
                ))}
                </StyledPaper>
            </Box>

      <div className="chat">
      {messages.map((msg, index) => (
        <Alert icon={<CheckIcon fontSize="inherit" />} severity="success" key={index}>
            {formatDate(msg.date)} - {msg.message}
        </Alert>
        ))}
      </div>
      <div className="input">
        <input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
        />
        <button onClick={sendMessage}>Send</button>
      </div>
      </Container>
    </div>
  );
};

export default Chat;
